import styles from "./styles.module.css";
import { ConstructorElement } from "@ya.praktikum/react-developer-burger-ui-components";

function BurgerConstructor(props) {
  const { success, ...otherProps } = props;
  const elements = otherProps.data;
  console.log(elements);

  return (
    <div className={styles.box}>
      {elements.map((element) => {
        if (element.type === "bun") {
          return (
            <li className={styles.element} key={element._id}>
              <img
                src={element.image}
                alt={element.name}
                className="pt-6"
              ></img>
              <div className={styles.priceIconWrapper}>
                <span className={`${styles.price} text text_type_main-default`}>
                  {element.price}
                </span>
                <CurrencyIcon />
              </div>
              <p className={`${styles.label}text text_type_main-default`}>
                {element.name}
              </p>
            </li>
          );
        }
      })}
    </div>
  );
}

export default BurgerConstructor;

