import styles from "./styles.module.css";
import MenuItems from "../MenuItems/MenuItems";
import ProductContainer from "../ProductContainer/ProductContainer";
import IngredientDetails from "../IngredientDetails/IngredientDetails";
import Modal from "../Modal/Modal";
import BurgerIngredient from "../BurgerIngredient/BurgerIngredient";

import { useState } from "react";

function BurgerIngredients(props) {
  const { elements, handleOpenModal, handleCloseModal, isOpen, ...otherProps } =
    props;

  const [currentIngredient, setCurrentIngredient] = useState(null);

  /* mock */
  const firstElement = elements[0];
  function mockFunc() {
    handleOpenModal();
    setCurrentIngredient("test");
  }
  /* mock */

  return (
    <>
      <div className={styles.box}>
        <p className="text text_type_main-large pt-10 pb-5">Соберите бургер</p>
        <MenuItems {...otherProps} />

        <ProductContainer elements={elements} />

        <BurgerIngredient ingredient={firstElement} key={firstElement._id} />

        {/* <BurgerIngredient ingredient={ingredient} onSelect={setCurrentIngredient} key={} /> */}
      </div>
      {currentIngredient && (
        <Modal
          header={"Детали ингредиента"}
          handleCloseModal={handleCloseModal}
          isOpen={isOpen}
        >
          <IngredientDetails ingredient={firstElement} />
        </Modal>
      )}
    </>
  );
}

export default BurgerIngredients;

