import styles from "./styles.module.css";
import { CurrencyIcon } from "@ya.praktikum/react-developer-burger-ui-components";

function BurgerIngredient(props) {
  const { ingredient, setCurrentIngredient } = props;

  function handleOnSelect() {
    setCurrentIngredient(ingredient);
  }
  return (
    <>
      <li className={styles.ingredient} onClick={onSelect}>
        <img
          src={ingredient.image}
          alt={ingredient.name}
          className="pt-6"
        ></img>
        <div className={styles.priceIconWrapper}>
          <span className={`${styles.price} text text_type_main-default`}>
            {ingredient.price}
          </span>
          <CurrencyIcon />
        </div>
        <p className={`${styles.label}text text_type_main-default`}>
          {ingredient.name}
        </p>
      </li>
    </>
  );
}

export default BurgerIngredient;
