import { createPortal } from "react-dom";

const modalRoot = document.getElementById("react-modals");

function Modal() {
  const [domReady, setDomReady] = React.useState(false);

  React.useEffect(() => {
    setDomReady(true);
  });

  return createPortal(
    <>
      <ModalOverlay>
        <div>test</div> {/* передаем контент */}
      </ModalOverlay>
      <Backdrop />
    </>,
    modalRoot
  );
}

export default Modal;
