            <img src={element.image} alt={element.name} className="pt-6"></img>
            <div className={stylesElementItem.priceIconWrapper}>
              <span
                className={`${stylesElementItem.price} text text_type_main-default`}
              >
                {element.price}
              </span>

              <CurrencyIcon />
            </div>
            <p
              className={`${stylesElementItem.label}text text_type_main-default`}
            >
              {element.name}
            </p>
          </li>
        </ul>
      ))}
    </div>
  ); */

  return (
    <div className={stylesElementItem.box}>
      <img src={data[0].image} alt={data[0].name} className="pt-6"></img>
      <div className={stylesElementItem.priceIconWrapper}>
        <CurrencyIcon />
        <span
          className={`${stylesElementItem.price} text text_type_main-default`}
        >
          {data[0].price}
        </span>
      </div>
      <p className={`${stylesElementItem.label}text text_type_main-default`}>
        {data[0].name}
      </p>
    </div>
  );
}

export default ElementItem;

