import stylesElementItem from "./stylesElementItem.module.css";
import { CurrencyIcon } from "@ya.praktikum/react-developer-burger-ui-components";

function ElementItem(props) {
  const { data, ...otherProps } = props;

  return (
    <>
      <h2>Булки</h2>
      <ul className={stylesElementItem.container}>
        {props.data.map((element) => {
          if (element.type === "bun") {
            return (
              <div key={element._id}>
                <div>render element</div>
              </div>
            );
          }
        })}
      </ul>
    </>
  );
}

export default ElementItem;

