import styles from "./styles.module.css";
import { ConstructorElement } from "@ya.praktikum/react-developer-burger-ui-components";
import { DragIcon } from "@ya.praktikum/react-developer-burger-ui-components";
import { CurrencyIcon } from "@ya.praktikum/react-developer-burger-ui-components";
import { Button } from "@ya.praktikum/react-developer-burger-ui-components";

function BurgerConstructor(props) {
  const { elements, handleOpenModal } = props;

  function openModalOrderDetails(e) {
    if (handleOpenModal) {
      handleOpenModal();
    }
  }

  return (
    <>
      <div className={styles.box + " pt-25"}>
        <div className={`${styles.scrollContainer} custom-scroll`}>
          <ul className={styles.list}>
            {elements.map((element) => {
              return (
                <li
                  className={`${styles.dragIconConstructorElementWrapper} mb-4`}
                  key={element._id}
                >
                  <DragIcon />

                  <ConstructorElement
                    isLocked={true}
                    text={element.name}
                    price={element.price}
                    thumbnail={element.image}
                  />
                </li>
              );
            })}
          </ul>
        </div>
        <div className={styles.bottomElementsWrapper + " mt-10"}>
          <p className="text text_type_digits-medium">610</p>
          <span className="ml-2 mr-10">
            <CurrencyIcon type="primary" />
          </span>
          <Button
            htmlType="button"
            type="primary"
            size="medium"
            onClick={openModalOrderDetails}
          >
            Оформить заказ
          </Button>
        </div>
      </div>
    </>
  );
}

export default BurgerConstructor;

<Modal header={"test"} handleCloseModal={handleCloseModal} isOpen={isOpen}>
  <div className={stylesApp.modalContaner}>
    <p className="text text_type_digits-large pt-30">034536</p>
    <p className="text text text_type_main-default mb-8">
      идентификатор заказа
    </p>
    <img src={imageDone} />
    <p className="text text text_type_main-default mt-15">
      Ваш заказ начали готовить
    </p>

    <p className="text text text_type_main-default mt-2 pb-30">
      Дождитесь готовности на орбитальной станции
    </p>
  </div>
</Modal>;
