import stylesElementItem from "./stylesElementItem.module.css";
import { CurrencyIcon } from "@ya.praktikum/react-developer-burger-ui-components";

function ElementItem(props) {
  const { data, ...otherProps } = props;

  return (
    <>
      <h2>Булки</h2>
      <ul className={stylesElementItem.container}>
        {props.data.map((element) => {
          if (element.type === "bun") {
            return (
              <div key={element._id}>
                <li className={stylesElementItem.element} key={element._id}>
                  <img
                    src={element.image}
                    alt={element.name}
                    className="pt-6"
                  ></img>
                  <div className={stylesElementItem.priceIconWrapper}>
                    <span
                      className={`${stylesElementItem.price} text text_type_main-default`}
                    >
                      {element.price}
                    </span>
                    <CurrencyIcon />
                  </div>
                  <p
                    className={`${stylesElementItem.label}text text_type_main-default`}
                  >
                    {element.name}
                  </p>
                </li>
              </div>
            );
          }
        })}
      </ul>
    </>
  );
}

export default ElementItem;

